//var myApp = angular.module('blogApp', ['ngRoute']);

myApp.config(['$routeProvider', function($routeProvider){
    $routeProvider
        .when('/',{
            // location of the template
        	templateUrl		: 'views/index-view.html',
        	// Which controller it should use
            controller 		:'LeagueController',
            // what is the alias of that controller.
        	controllerAs 	: 'myLeague'
        })
        .when('/view-match/:date/:team1code/vs/:team2code',{
          templateUrl   : 'views/match-view.html',
          controller 		: 'singleMatchController',
          controllerAs 	: 'myMatch'
        })
        .when('/team-wise/:teamcode/:teamkey', {
            templateUrl: 'views/team-view.html',
            controller: 'teamWiseController',
            controllerAs: 'myTeam'
        })
        .otherwise(
            {
                //redirectTo:'/'
                template   : '<h1>404 page not found</h1>'
            }
        );

}]);
